export default function FormatDate ({value}) {
    const clickedDate = value.toString()
    //for now, we need to formar Mar to 03, etc

    const getMonthFromString = () => {
        return clickedDate.slice(4,7);
    }

    //need to format the first part of this string => jan = 1 feb = 2
    const formatMonthToInt = () => {
        const month = getMonthFromString()
        if(month === "Jan") {
            return "01"
        }   if(month === "Feb") {
            return "02"
        }   if(month === "Mar") {
            return "03"
        }   if(month === "Apr") {
            return "04"
        }   if(month === "May") {
            return "05"
        }   if(month === "Jun") {
            return "06"
        }   if(month === "Jul") {
            return "07"
        }   if(month === "Aug") {
            return "08"
        }   if(month === "Sep") {
            return "09"
        }   if(month === "Oct") {
            return "10"
        }   if(month === "Nov") {
            return "11"
        }   if(month === "Dec") {
            return "12"
        } else {
            alert("wrong date information passed into function")
        }
    }

    //above function returns "03"

    //remember we need MMDDYYYY as format 


        const formatDateToString = () => {
            const monthToInt = formatMonthToInt();
            const dayAndYearToString = 
                (clickedDate.slice(8,10) + clickedDate.slice(11,15)) //should return "032021" MMDDYYYY
            //key string for DB
            const formattedYear = monthToInt + dayAndYearToString;
            return formattedYear
        }

    console.log(formatDateToString());
}
