export default function SubmitNewEntry (date, description) {

    const submitNewEntry = async() => {
        try {

            //check for duplicate values first 
                //if date exists in DB, throw alert (we will update the alert part later)
                //this logic is created in EXPRESS SERVER - typeorm?

            const body = {date, description}
            const response = await fetch("http://localhost:5000/entry", {
                method : "POST",
                headers: {"Content-Type" : "application/json"},
                body: JSON.stringify(body)
            })

        } catch (err) {
            console.error(err.message)
        }
    }
    return submitNewEntry()
}
